import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ShoppingCart, Heart, Star, Send, Loader2, ArrowRight } from 'lucide-react'; // Added icons
import { getProduct, getSimilarProducts, createReview } from '../services/api';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-toastify';
import ProductCard from '../components/ProductCard';
import ChatBot from '../components/ChatBot';

const ProductDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { addToCart } = useCart();
    const { user } = useAuth();

    const [product, setProduct] = useState(null);
    const [similarProducts, setSimilarProducts] = useState([]);
    const [selectedSize, setSelectedSize] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [loading, setLoading] = useState(true);
    const [reviewRating, setReviewRating] = useState(5);
    const [reviewComment, setReviewComment] = useState('');
    const [submittingReview, setSubmittingReview] = useState(false);
    // State to handle image selection/gallery
    const [mainImage, setMainImage] = useState(''); 

    // Combined fetch function wrapped in useCallback
    const fetchData = useCallback(async () => {
        setLoading(true);
        try {
            const { data: productData } = await getProduct(id);
            setProduct(productData.data);

            // Set initial state based on fetched data
            if (productData.data.sizes && productData.data.sizes.length > 0) {
                setSelectedSize(productData.data.sizes[0]);
            }
            if (productData.data.images && productData.data.images.length > 0) {
                setMainImage(productData.data.images[0].url); // Set the first image as main
            }

            const { data: similarData } = await getSimilarProducts(id);
            setSimilarProducts(similarData.data);

        } catch (error) {
            console.error('Error fetching product:', error);
            toast.error('Product not found or error loading data.');
            navigate('/shop');
        } finally {
            setLoading(false);
        }
    }, [id, navigate]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);


    const handleAddToCart = () => {
        if (!selectedSize) {
            toast.error('Please select a size');
            return;
        }
        addToCart(product, selectedSize, quantity);
        toast.success(`${quantity}x ${product.name} added to cart!`);
    };

    const handleSubmitReview = async (e) => {
        e.preventDefault();
        if (!user) {
            toast.error('Please login to submit a review');
            navigate('/login');
            return;
        }

        setSubmittingReview(true);
        try {
            await createReview(id, { rating: reviewRating, comment: reviewComment });
            toast.success('Review submitted successfully!');
            setReviewComment('');
            setReviewRating(5);
            fetchData(); 
        } catch (error) {
            toast.error(error.response?.data?.message || 'Failed to submit review');
        } finally {
            setSubmittingReview(false);
        }
    };

    if (loading) {
        return <p className="text-center py-16 text-xl text-indigo-600">Loading your style inspiration...</p>;
    }

    if (!product) return null;

    // Helper component for Star Rating display
    const StarRating = ({ rating }) => {
        const fullStars = Math.round(rating);
        return (
            <div className="flex items-center gap-0.5">
                {Array.from({ length: 5 }, (_, i) => (
                    <Star 
                        key={i} 
                        className={`w-5 h-5 ${i < fullStars ? 'text-yellow-400 fill-yellow-400' : 'text-gray-300'}`} 
                    />
                ))}
            </div>
        );
    };

    return (
        <div className="product-detail-page max-w-7xl mx-auto px-4 py-12 space-y-12">

            {/* Product Section: Grid layout for Image Gallery and Details/Actions */}
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10">

                {/* Image Gallery (Left Column - Spanning 2/3rds for a premium feel) */}
                <div className="lg:col-span-2">
                    <div className='bg-white p-4 rounded-xl shadow-xl border border-gray-100'>
                        {/* Main Image */}
                        <div className="relative w-full h-[400px] sm:h-[600px] mb-4">
                            {mainImage ? (
                                <img
                                    src={mainImage}
                                    alt={product.name}
                                    className="w-full h-full object-cover rounded-lg transition duration-300 ease-in-out hover:scale-[1.02] transform"
                                />
                            ) : (
                                <div className="w-full h-full bg-gray-200 rounded-lg flex items-center justify-center text-6xl text-gray-400">


[Image of a beautiful dress on a model]

                                </div>
                            )}
                        </div>

                        {/* Thumbnail Selector */}
                        {product.images?.length > 1 && (
                            <div className="flex gap-3 overflow-x-auto pb-2">
                                {product.images.map((img, index) => (
                                    <img
                                        key={index}
                                        src={img.url}
                                        alt={`Thumbnail ${index + 1}`}
                                        className={`w-20 h-20 object-cover rounded-lg cursor-pointer transition border-2 ${
                                            mainImage === img.url 
                                                ? 'border-indigo-600 shadow-md' 
                                                : 'border-gray-300 opacity-80 hover:opacity-100 hover:border-indigo-400'
                                        }`}
                                        onClick={() => setMainImage(img.url)}
                                    />
                                ))}
                            </div>
                        )}
                    </div>
                </div>

                {/* Product Info & Actions (Right Column - Fixed sticky for quick access) */}
                <div className="lg:col-span-1 space-y-6 lg:sticky lg:top-8 h-fit">
                    <h1 className="text-4xl font-extrabold text-gray-900">{product.name}</h1>

                    {/* Rating and Price */}
                    <div className="flex items-center gap-3 border-b pb-4">
                        <StarRating rating={product.rating || 0} />
                        <span className="text-gray-600 text-sm font-medium">
                            {product.rating?.toFixed(1) || '0.0'} ({product.reviews?.length || 0} reviews)
                        </span>
                    </div>

                    <p className="text-5xl font-extrabold text-pink-600">${product.price.toFixed(2)}</p>

                    <p className="text-gray-700 leading-relaxed">{product.description}</p>

                    {/* Size Selector */}
                    {product.sizes && product.sizes.length > 0 && (
                        <div>
                            <label className="font-bold text-gray-800 mb-2 block">Choose Size:</label>
                            <div className="flex flex-wrap gap-3">
                                {product.sizes.map((size) => (
                                    <button
                                        key={size}
                                        onClick={() => setSelectedSize(size)}
                                        className={`px-4 py-2 border-2 rounded-full font-medium transition ${
                                            selectedSize === size
                                                ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg'
                                                : 'bg-white border-gray-300 text-gray-700 hover:bg-indigo-50 hover:border-indigo-500'
                                            }`}
                                    >
                                        {size}
                                    </button>
                                ))}
                            </div>
                            {!selectedSize && <p className="text-red-500 text-sm mt-2">Please select a size to proceed.</p>}
                        </div>
                    )}

                    {/* Quantity */}
                    <div>
                        <label className="font-bold text-gray-800 mb-2 block">Quantity:</label>
                        <input
                            type="number"
                            min="1"
                            max="10" 
                            value={quantity}
                            onChange={(e) => setQuantity(Math.max(1, Math.min(10, Number(e.target.value))))}
                            className="w-24 border border-gray-300 rounded-lg px-3 py-2 text-center focus:border-indigo-500 focus:ring-indigo-500"
                        />
                    </div>

                    {/* Actions */}
                    <div className="mt-8 space-y-4">
                        <button
                            onClick={handleAddToCart}
                            disabled={!selectedSize}
                            className="w-full btn-primary bg-indigo-600 hover:bg-indigo-700 text-white text-lg py-3 rounded-xl transition shadow-lg flex items-center justify-center gap-3 disabled:bg-gray-400"
                        >
                            <ShoppingCart size={20} /> Add to Bag
                        </button>
                        <button className="w-full py-3 text-indigo-600 border border-indigo-200 rounded-xl hover:bg-indigo-50 transition flex items-center justify-center gap-3">
                            <Heart size={20} /> Add to Wishlist
                        </button>
                    </div>
                </div>
            </div>

            <hr className='my-10 border-gray-200' />

            {/* Reviews Section: Split layout for Reviews List and Submission Form */}
            <section className="grid lg:grid-cols-3 gap-10">
                <div className='lg:col-span-2'>
                    <h2 className="text-3xl font-bold text-gray-800 mb-6 border-b pb-3">Customer Reviews ({product.reviews?.length || 0})</h2>

                    {/* Existing Reviews List */}
                    {product.reviews && product.reviews.length > 0 ? (
                        <div className="space-y-5 max-h-96 overflow-y-auto pr-3">
                            {product.reviews.map((review) => (
                                <div key={review._id} className="p-4 bg-white rounded-lg shadow border border-gray-100 hover:shadow-md transition">
                                    <div className="flex items-center justify-between mb-2 border-b pb-2">
                                        <div className="flex items-center gap-3">
                                            <StarRating rating={review.rating} />
                                            <span className="text-sm font-semibold text-gray-800">
                                                {review.user?.name || 'Anonymous Fashionista'}
                                            </span>
                                        </div>
                                        <p className="text-xs text-gray-400">
                                            {/* You might want to add review creation date here */}
                                        </p>
                                    </div>
                                    <p className="text-gray-700 italic mt-2">"{review.comment}"</p>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p className="text-gray-500 text-lg">Be the first to review this stunning dress!</p>
                    )}

                </div>

                Review Form (Sticky on desktop)
                <div className="lg:col-span-1 bg-indigo-50 p-6 rounded-xl shadow-inner border border-indigo-200 sticky lg:top-8 h-fit">
                    <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
                        <Send className='w-5 h-5 text-indigo-600' /> Submit Your Review
                    </h3>
                    <form onSubmit={handleSubmitReview} className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700">Rating:</label>
                            <select
                                value={reviewRating}
                                onChange={(e) => setReviewRating(Number(e.target.value))}
                                className="w-full border border-gray-300 rounded-lg px-3 py-2 mt-1 focus:border-indigo-500 focus:ring-indigo-500"
                            >
                                {[5, 4, 3, 2, 1].map((num) => (
                                    <option key={num} value={num}>
                                        {num} Star{num > 1 ? 's' : ''}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <textarea
                            value={reviewComment}
                            onChange={(e) => setReviewComment(e.target.value)}
                            placeholder="Share your thoughts on the dress, fit, and quality..."
                            className="w-full border border-gray-300 rounded-lg p-3 h-24 focus:border-indigo-500 focus:ring-indigo-500"
                            required
                        />
                        <button
                            type="submit"
                            disabled={submittingReview}
                            className="w-full btn-primary bg-indigo-600 hover:bg-indigo-700 text-white py-3 rounded-lg flex items-center justify-center gap-2 transition"
                        >
                            {submittingReview ? (
                                <><Loader2 className="w-5 h-5 animate-spin" /> Submitting...</>
                            ) : (
                                <>Post Review <ArrowRight className='w-4 h-4' /></>
                            )}
                        </button>
                        {!user && <p className='text-xs text-red-600 mt-2 text-center'>*Log in to share your feedback.</p>}
                    </form>
                </div>
            </section>

            {/* Similar Products */}
            {similarProducts.length > 0 && (
                <section>
                    <h2 className="text-3xl font-bold text-gray-800 mb-6">
                        AI's Top Picks: Similar Styles
                    </h2>
                    <div className="grid sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                        {similarProducts.slice(0, 4).map((sp) => (
                            <ProductCard key={sp._id} product={sp} />
                        ))}
                    </div>
                </section>
            )}

            {/* Chatbot */}
            <ChatBot />
        </div>
    );
};

export default ProductDetails;
